  simde__m128i test = simde_mm_maskload_epi32(ptr, mask);
  int32_t r[4] = { INT32_C(0), INT32_C(0), INT32_C(0), INT32_C(0) };
  simde_test_x86_assert_equal_i32x4(test, simde_mm_loadu_epi32(r));
